const clicking=e=>{if(e.target.classList.contains("showDetail")){const n=e.target.closest("tr").querySelector("td:first-child").innerText;e.preventDefault();const t=new XMLHttpRequest;t.onreadystatechange=(()=>{if(4===t.readyState&&200===t.status){document.querySelector(".userstable .detail_error").classList.remove("active");const e=JSON.parse(t.response),n=document.querySelector(".userstable .detail_info"),r=e.address.city+","+e.address.street+" "+e.address.suite+" "+e.address.zipcode,s=e.address.geo.lat+","+e.address.geo.lng;n.querySelector(".detail_info__id span").innerHTML=e.id,n.querySelector(".detail_info__name span").innerHTML=e.name,n.querySelector(".detail_info__username span").innerHTML=e.username,n.querySelector(".detail_info__email span").innerHTML=e.username,n.querySelector(".detail_info__address span").innerHTML=r,n.querySelector(".detail_info__latlng span").innerHTML=s,n.querySelector(".detail_info__phone span").innerHTML=e.phone,n.querySelector(".detail_info__website span").innerHTML=e.website,n.querySelector(".detail_info__company span").innerHTML=e.company.name,n.querySelector(".detail_info__company-phrase span").innerHTML=e.company.catchPhrase,n.querySelector(".detail_info__company-bs span").innerHTML=e.company.bs}else if(4===t.readyState){const e=document.querySelector(".userstable .detail_error");e.classList.add("active"),e.innerHTML="Ошибка получения данных"}}),t.open("GET","https://jsonplaceholder.typicode.com/users/"+n),t.send()}};document.querySelector(".userstable").addEventListener("click",clicking);